    public RegisterScreen() {
        initComponents();
        setExtendedState(MAXIMIZED_BOTH);
        getContentPane().setBackground(new Color(255,255,255));
        for(int i=1;i<=100;i++) 
        {
            age_selector.addItem(i+"");
        }
        age_selector.setSelectedIndex(-1);
        country_selector.setSelectedIndex(-1);
        db = new DBConnection();
        con = db.createConnection();
        if(con!=null)
        {
            try
                {
                    int i;
                    PreparedStatement pst = con.prepareStatement("select Customer_ID from customer order by Customer_ID DESC LIMIT 1");
                    ResultSet rs = pst.executeQuery();
                    if(rs.next()){
                        id = rs.getString(1);
                        customer_id.setText(Integer.toString(Integer.parseInt(id)+1));
                    }
                }
                catch(Exception e){
                    JOptionPane.showMessageDialog(null, e);
                }
        }
        customer_id.setEditable(false);
        //jPanel1.setVisible(false);
        //jPanel3.setVisible(true);

    }
    
