    private void create_accountActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_create_accountActionPerformed
        if(customer_name.getText().length()==0 || create_username.getText().length()==0 || create_password.getText().length()==0 || retype_password.getText().length()==0 || (male.isSelected() == false && female.isSelected() == false) || country_selector.getSelectedIndex() == -1 || age_selector.getSelectedIndex() == -1){
            JOptionPane.showMessageDialog(null, "Empty fields detected ! Please fill up all fields");
        }
        else{
            boolean flag = true;
            String name = customer_name.getText();
            String age = (String) age_selector.getSelectedItem();
            String gen = gender.getSelection().getActionCommand();
            String country = (String) country_selector.getSelectedItem();
            String username = create_username.getText();
            String pass = create_password.getText();
            String re_pass = retype_password.getText();
            String pattern = "(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=])(?=\\S+$).{8,}";
            if(!pass.matches(pattern)){
                JOptionPane.showMessageDialog(null, "Password should be minimum 8 Characters which consist at least one symbol,Capital letter and digit");
            }
            else{
                if(!create_password.getText().equals(retype_password.getText())){
                    JOptionPane.showMessageDialog(null, "Password and Retyped password doesn't match");
                }
                else{
                    if(con!=null)
                    {
                        try
                        {
                            PreparedStatement pst = con.prepareStatement("select username from customer where username=?");
                            pst.setString(1, username);
                            ResultSet rs = pst.executeQuery();
                            if(rs.next()){
                                flag = false;
                                JOptionPane.showMessageDialog(null, "username exist try another");
                            }
                            else{
                                flag = true;
                                JOptionPane.showMessageDialog(null, "proceed");
                            }
                        }
                        catch(Exception e){
                            JOptionPane.showMessageDialog(null, e);
                        }
                        if(flag == true){
                            try
                            {
                                PreparedStatement pst = con.prepareStatement("insert into customer values (DEFAULT,?, ?, ?, ?, ?, md5(?))");
                                pst.setString(1, name); 
                                pst.setString(2, gen);
                                pst.setString(3, age);
                                pst.setString(4, country);
                                pst.setString(5, username);
                                pst.setString(6, pass);
                                pst.execute();
                                JOptionPane.showMessageDialog(null, "Account Created");
                                con.close();
                            }
                            catch(Exception e){
                                JOptionPane.showMessageDialog(null, e);
                            }
                        }
                        flag = false;
                        customer_name.setText(null);
                        age_selector.setSelectedIndex(-1);
                        country_selector.setSelectedIndex(-1);
                        male.setSelected(false);
                        female.setSelected(false);
                        create_username.setText(null);
                        create_password.setText(null);
                        retype_password.setText(null);
                    }
                }
            }
        }
    }//GEN-LAST:event_create_accountActionPerformed

